def checkadb():
	res = os.popen('adb.exe devices')
	res_read = res.read()
	if res_read.split('\n')[1] == '':
		# First returned argument is for adb not running, second is for no root (only set as True with MEmu).
		os.system('taskkill /F /IM adb.exe')
		return False, False
	else:
		if res_read.split('\n')[1].split(':')[1].split('\t')[0] == '21503':
			return True, True
		return True, False


