def findall(a_str, sub):
	start = 0
	found_list = []
	while start <= len(a_str)-len(sub):
		start = a_str.find(sub, start)
		if start == -1: 
			break
		found_list.append(start)
		#print(found_list)
		start += 1
	for x in found_list:
		try:
			if found_list.find(x+1) > -1:
				found_list.pop(found_list.index(x+1))
		except:
			continue
	return found_list
	
	
